css:
body { font-family: 'Arial', sans-serif; direction: rtl; }
  .elegant-header { text-align: center; border-bottom: 2px solid #333; padding-bottom: 10px; margin-bottom: 20px; }
  .elegant-header h2 { color: #2c3e50; margin: 0; font-size: 22px; }
  .elegant-header p { color: #7f8c8d; margin: 4px 0; font-size: 12px; }
  .section-title { background: #2c3e50; color: #fff; padding: 5px 10px; font-size: 13px; margin: 12px 0 6px 0; }
  .info-table { width: 100%; border-collapse: collapse; margin-bottom: 10px; }
  .info-table td { padding: 5px 8px; font-size: 12px; border: 1px solid #ddd; }
  .info-table td:first-child { font-weight: bold; background: #f8f9fa; width: 35%; }
  .amount-box { text-align: center; background: #ecf0f1; border: 2px solid #2c3e50; padding: 10px; margin: 15px 0; }
  .amount-box .amount-label { font-size: 12px; color: #7f8c8d; }
  .amount-box .amount-value { font-size: 24px; font-weight: bold; color: #2c3e50; }
  .footer { margin-top: 20px; border-top: 1px solid #ddd; padding-top: 10px; font-size: 10px; color: #95a5a6; text-align: center; }
  .status-badge { display: inline-block; padding: 2px 8px; border-radius: 3px; font-size: 11px; font-weight: bold; }
  .status-green { background: #d4edda; color: #155724; }
  .status-orange { background: #fff3cd; color: #856404; }

