  .accounts-table { width: 100%; border-collapse: collapse; margin-bottom: 10px; font-size: 12px; }
  .accounts-table th { background: #2c3e50; color: #fff; padding: 6px 8px; text-align: right; }
  .accounts-table td { padding: 5px 8px; border: 1px solid #ddd; }
  .accounts-table tr:nth-child(even) { background: #f8f9fa; }
  .totals-row { font-weight: bold; background: #ecf0f1 !important; }
  .footer { margin-top: 20px; border-top: 1px solid #ddd; padding-top: 10px; font-size: 10px; color: #95a5a6; text-align: center; }

